package com.app.stack;

import com.app.core.Customer;

public class FixedStack implements Stack {
	
	private Customer[] customerArray = new Customer[STACK_SIZE] ;
	private int top = -1 ; // top is -1, meaning the                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      

@Override 
public boolean isFull() { // Check if the stack is full 
	if (top == STACK_SIZE-1) 
		return true;
	else 
		return false ;
}
@Override
public boolean isEmpty() { // Check if stack is empty 
	if (top == -1)
		return true ;
	else 
		return false ;
}
@Override
public void push(Customer c) { 
	if (! isFull()) {
		customerArray[++top] = c;
		System.out.println("push success!");
		System.out.println(customerArray[top] + " ,pushed to stack.") ;
	}else 
		System.out.println("Stack Overflow!");	
}
@Override
public Customer pop() {
	if (!isEmpty()) {
		System.out.println("Pop Success!");
		System.out.println(customerArray[top] + " popped from stack");
		return customerArray[top--] ;
		

	}else {
		System.out.println("Stack underflow");
		return null ;
	}
	
}

}